        and jm_xj_mf.jb_no like CONCAT('%',#{otherId},'%')


        and jm_xj_mf.xjid <> #{notType}


        and sorg.orgcode <> #{notSorg}


        and dev.dev_no = #{devNo}


        and dev.devid = #{devid}


        and jm_xj_mf.md_no = #{mouldNo}



    ORDER BY ${descOrder} DESC


    ORDER BY ${ascOrder} ASC

