select xjmf.sid,xjmf.jb_no,xjmf.prd_no,prdt.name as prd_name,mould.name,xjmf.test_bo,xjmf.first_chk,ristxjmf.sid as rist_sid,ristxjmf.first_chk as rist_chk,xjmf.smake_name,isnull(xjmf.state,0) as state,xjmf.hpdate from jm_xj_mf xjmf left join (select * from jm_xj_mf ristxjmf where ristxjmf.xjid='2') ristxjmf on xjmf.jb_no=ristxjmf.jb_no and datediff(day,ristxjmf.hpdate,xjmf.hpdate) = 0 left join jm_mould mould on xjmf.md_no=mould.md_no left join jm_prdt prdt on xjmf.prd_no=prdt.prd_no
xjmf.xjid='1'
and xjmf.rs_no = #{devNo}
and xjmf.hpdate >= #{begDd}
and xjmf.hpdate <= #{endDd}
ORDER BY ${descOrder} DESC
ORDER BY ${ascOrder} ASC
